def recherche_sequentielle(tab, valeur):
    for i in range(len(tab)):
        if tab[i] == valeur:
            return i
    return -1

def tri_bulles(tab):
    n = len(tab)
    for i in range(n):
        for j in range(0, n-i-1):
            if tab[j] > tab[j+1]:
                tab[j], tab[j+1] = tab[j+1], tab[j]
    return tab

def tri_selection(tab):
    for i in range(len(tab)):
        min_idx = i
        for j in range(i+1, len(tab)):
            if tab[j] < tab[min_idx]:
                min_idx = j
        tab[i], tab[min_idx] = tab[min_idx], tab[i]
    return tab

def tri_insertion(tab):
    for i in range(1, len(tab)):
        x = tab[i]
        j = i
        while j > 0 and tab[j-1] > x:
            tab[j] = tab[j-1]
            j -= 1
        tab[j] = x
    return tab

def fusion(t1,t2):
    i=j=0; res=[]
    while i<len(t1) and j<len(t2):
        if t1[i]<=t2[j]: res.append(t1[i]); i+=1
        else: res.append(t2[j]); j+=1
    res.extend(t1[i:])
    res.extend(t2[j:])
    return res
def tri_fusion(tab):
    if len(tab)<=1: return tab
    m=len(tab)//2
    return fusion(tri_fusion(tab[:m]), tri_fusion(tab[m:]))

