def crible(n):
    p = [True]*(n+1)
    p[0]=p[1]=False
    for i in range(2,int(n**0.5)+1):
        if p[i]:
            for j in range(i*i,n+1,i): p[j]=False
    return [i for i in range(n+1) if p[i]]

def syracuse(n):
    c = 0
    while n > 1:
        n = n//2 if n%2==0 else 3*n+1
        c += 1
