def levenshtein(a,b):
    m,n = len(a),len(b)
    dp = [[0]*(n+1) for _ in range(m+1)]
    for i in range(m+1): dp[i][0] = i
    for j in range(n+1): dp[0][j] = j
    for i in range(1,m+1):
        for j in range(1,n+1):
            cost = 0 if a[i-1]==b[j-1] else 1
            dp[i][j] = min(dp[i-1][j]+1, dp[i][j-1]+1, dp[i-1][j-1]+cost)
    return dp[m][n]

def sac_a_dos(poids,valeurs,W):
    dp = [0]*(W+1)
    for i in range(len(poids)):
        for w in range(W, poids[i]-1, -1):
            dp[w] = max(dp[w], dp[w-poids[i]] + valeurs[i])
    return dp[W]

def kadane(tab):
    max_actuel = max_global = tab[0]
    for x in tab[1:]:
        max_actuel = max(x, max_actuel + x)
        if max_actuel > max_global:
            max_global = max_actuel
    return max_global

def rendu_monnaie(somme, pieces=[100,50,20,10,5,2,1]):
    pieces.sort(reverse=True)
    res = []
    for p in pieces:
        while somme >= p:
            res.append(p)
            somme -= p
    return res

def selection_activites(activites):
    activites.sort(key=lambda x: x[1])
    sel = [activites[0]]
    fin = activites[0][1]
    for deb,fin_act in activites[1:]:
        if deb >= fin:
            sel.append((deb,fin_act))
            fin = fin_act
    return sel

