def phi(n):
    res = n
    p = 2
    while p*p <= n:
        if n % p == 0:
            res = res // p * (p-1)
            while n % p == 0: n //= p
        p += 1
    if n > 1: res = res // n * (n-1)
    return res

def racine_entier(n):
    x = n
    while x*x > n:
        x = (x + n//x) // 2
    return x

