def permutations(lst):
    if len(lst) <= 1: return [lst]
    res = []
    for i,x in enumerate(lst):
        for p in permutations(lst[:i]+lst[i+1:]):
            res.append([x]+p)
    return res

def knapsack_fractionnaire(poids,valeurs,W):
    objs = sorted(range(len(poids)), key=lambda i:valeurs[i]/poids[i], reverse=True)
    profit = 0
    for i in objs:
        if poids[i] <= W:
            profit += valeurs[i]
            W -= poids[i]
        else:
            profit += valeurs[i] * (W / poids[i])
            break
    return profit

