def recherche_sequentielle(tab, valeur):
    for i in range(len(tab)):
        if tab[i] == valeur:
            return i
    return -1

def tri_bulles(tab):
    n = len(tab)
    for i in range(n):
        for j in range(0, n-i-1):
            if tab[j] > tab[j+1]:
                tab[j], tab[j+1] = tab[j+1], tab[j]
    return tab
